import React, {useEffect, useState} from "react";
import "./assets/Styles.css";
import {loadAllPokemons, loadImage} from "./LoadData";
import {DisplayPokemons} from "./components/DisplayPokemons";
import {Toolbar} from "./components/Toolbar";

function App() {


    let [images, setImages] = useState(new Map())
    let [loading, setLoading] = useState(true)
    let [pokemons, setPokemons] = useState(undefined)
    let [filter, setFilter] = useState("")
    let [sort, setSort] = useState("none");

    let keys = ["number", "name", "type_one", "type_two", "total", "hit_points", "attack", "defense", "special_attack", "special_defense", "speed", "generation", "legendary"]

    useEffect(() => {
        loadAllPokemons()
          .then(data => {
            let promisesArray = data.map( pokemon => {
                return loadImage(pokemon["name"].toLowerCase()).then(image => {
                    return {...pokemon, image};

                });
            })
            return Promise.all(promisesArray);
          })
            .then((data) => setPokemons(data));


    }, []);

    return (
        <div className="container">
            <h1>Pokedex</h1>
            <Toolbar setFilter={(value) => setFilter(value)} setSort={(value) => setSort(value)}></Toolbar>

            { !pokemons ? <div> loading...</div>:<DisplayPokemons keys={keys} pokemons={pokemons}></DisplayPokemons> }
        </div>

    );

}

export default App;
