import "./assets/Styles.css";
import React, {useEffect, useState} from "react";
import {
    loadPokemonsSize,
    loadPokemonsWithImages
} from "./utils/loadData";
import {DisplayPokemons} from "./components/DisplayPokemons";
import {Toolbar} from "./components/Toolbar/Toolbar";
import {Pagination} from "./components/Pagination";

let App = () => {
    const isSystemDark = window.matchMedia("(prefers-color-scheme: dark)").matches
    const [isDark,setIsDark] = useState(isSystemDark)
    const [dataPerPage, setDataPerPage] = useState(5)
    const [size, setSize] = useState(0)
    const [pokemons, setPokemons] = useState(undefined)
    const [filter, setFilter] = useState("")
    const [sort, setSort] = useState("none");
    const [page, setPage] = useState(1);

    const lightTheme = {backgroundColor: "white", color: "black"}
    const darkTheme = {backgroundColor : "black", color : "white"}
    let handlePageChanged = (value) => {
        setPage(value)
        // loadData(value)
    }
    let handleSort = (value) => {
        setPage(1)
        setSort(value)
        // loadData(1, dataPerPage, sort)

    }
    let handleFilter = (value) => {
        setPage(1)
        setFilter(value)
        // loadData(1, dataPerPage, sort, value)


    }

    let handleDataPerPage = (value) => {
        setPage(1)
        setDataPerPage(value)
        // loadData(1, dataPerPage)

    }
    let loadData = (page, dataPerPage=undefined, sort=undefined, filter=undefined) =>  {
        setPokemons(undefined)
         loadPokemonsWithImages(page, dataPerPage, sort, filter).then(data => {
            setPokemons(data)
        })
    }
    useEffect(() => {
        loadData(page, dataPerPage)
    }, [page, dataPerPage, sort, filter]);

    useEffect(() => {
        loadPokemonsSize().then(data => setSize(data ? data : 0))

    }, []);

    return (
        <div className="container" style={isDark ? darkTheme : lightTheme }>

            <Toolbar setFilter={(value) => handleFilter(value)}
                     setSort={(value) => handleSort(value)}
                     setDataPerPage={(value) => handleDataPerPage(value)}
                     setTheme={(value) => setIsDark(value)}>
            </Toolbar>

            <DisplayPokemons pokemons={pokemons}></DisplayPokemons>
            <Pagination setPage={(value) => handlePageChanged(value)}
                        page={page}
                        pages={filter ? 1 : Math.ceil(size/dataPerPage)}
                        loading={!pokemons}>
            </Pagination>
        </div>

    );

}

export default App;
