import {useEffect, useState} from "react";
import {loadAllPokemons, loadImage} from "../LoadData";

export let DisplayPokemons = ({pokemons, keys, filter, sort}) => {
    let [images, setImages] = useState(undefined)
    let [sortedData, setSortedData] = useState(pokemons)
      const sortMethods = {

    none: { method: (a, b) =>0 },
    ascending: { method: (a, b) => (a > b ? 1 : -1) },
    descending: { method: (a, b) => (a > b ? -1 : 1) },
  };
    let loadImages = () => {
        let imagesMap = new Map();
        for (let pokemon of pokemons) {
            loadImage(pokemon["name"].toLowerCase()).then(image => {
                imagesMap.set(pokemon["name"], image)

            })
         }
        setImages(imagesMap)
    }
    let DisplayAttributes = ({p}) => {
        return (

            <ul>
                {keys.map((key, index) => {
                return <li key={index}>{key}: {p[key]}</li>
                })}
            </ul>

        )
    }
    useEffect(() => {

        if (!pokemons || sort === "none") {
            setSortedData(pokemons)

        }
        else {
            setSortedData(pokemons.sort((p1,p2) => sortMethods[sort].method(p1["name"], p2["name"])))
            loadImages(pokemons)

        }

    }, [pokemons, sort]);



    return (
        <div >
            {sortedData.map((p, index)=> {
                return filter === "" || p["type_one"] === filter ?
                    <div key={index}>
                        <h4> {p.name}:</h4>
                        <DisplayAttributes p={p}></DisplayAttributes>
                        <img src={images.get(p["name"])} alt="imane" />

                    </div> : <></>
            })}
        </div>
    );
}
