export let loadAllPokemons = () => {
    fetch(`/api/`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }
            return res.text()
        })
        .then((data) => {
            return JSON.parse(data)
        })
        .catch((err) => {
            console.log('Error occurred while loading chat: ' + err)
        })
}

export let loadImage = (number) => {

    fetch(`/api/icon/${number}`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }
            return res.text()
        })
        .then((data) => {
            return data
        })
        .catch((err) => {
            console.log('Error occurred while loading chat: ' + err)
        })
}
