export let Pagination = ({ page, setPage, pages, loading}) => {

    return (
       <div className='footer' >
           {/*<button onClick={() => setPage( page-1)} disabled={page === 1 || loading} >prev</button>*/}
           {/*<div className='page-number'>{page}/{pages}</div>*/}
           {/*<button onClick={() => setPage(page+1)} disabled={page === pages || loading} >next</button>*/}
           {/* <input type="number" name="page"  min="1" max={pages} onChange={(event) => setPage(event.target.value) }/>*/}

           <nav aria-label="Page navigation example">
               <ul className="pagination">
                   <li className="page-item" onClick={() => setPage( page-1)} >
                       <a className="page-link" href="#" aria-label="Previous">
                           <span aria-hidden="true">&laquo;</span>
                           <span className="sr-only">Previous</span>
                       </a>
                   </li>
                   <li className="page-item"><a className="page-link" href="#">{page}</a></li>
                   <li className="page-item" onClick={() => setPage( page+1)} >
                       <a className="page-link" href="#" aria-label="Next">
                           <span aria-hidden="true">&raquo;</span>
                           <span className="sr-only">Next</span>
                       </a>
                   </li>
               </ul>
           </nav>
       </div>

    );
}
