import React, {useEffect, useState} from "react";
import "./assets/Styles.css";
import {Sort} from "./components/Sort";
import {DisplayPokemons} from "./components/DisplayPokemons";
import {Filter} from "./components/Filter";
import Pagination from 'react-bootstrap/Pagination';
function App() {



    let [pokemonImage, setPokemonImage] = useState(undefined)
    let [pokemons, setPokemons] = useState(undefined)
    let [filter, setFilter] = useState("")
    let [sortMethod, setSortMethod] = useState(undefined);

    let keys = ["number", "name", "type_one", "type_two", "total", "hit_points", "attack", "defense", "special_attack", "special_defense", "speed", "generation", "legendary"]

    useEffect(() => {
        loadAllPokemons()

    }, []);

    return (
        <div className="container">
            <h1>Pokedex</h1>

            <Sort  setSortMethod={value => setSortMethod(value)}></Sort>
            <Filter setFilter={value => setFilter(value)}></Filter>
            <DisplayPokemons keys={keys} pokemons={pokemons} filter={filter} sortMethod={sortMethod}></DisplayPokemons>

        </div>

    );

}

export default App;
