import React, { useState } from "react";
import SweetPagination from "sweetpagination";
import {checkImage} from "../utils/loadData";


export let DisplayPokemons = ({pokemons, filter, sort, dataPerPage}) => {
  const [currentPageData, setCurrentPageData] = useState(new Array(2).fill());

    const sortMethods = {
        none: { method: (a, b) => null },
        ascending: { method: (a, b) => (a > b ? 1 : -1)  },
        descending: { method: (a, b) => (a > b ? -1 : 1) },
  };
    let DisplayAttributes = ({pokemon, index}) => {
        if (pokemon === undefined) {
            return <></>
        }
        checkImage("https://img.pokemondb.net/sprites/silver/normal/bulbasaur.png")
        let keys = Object.keys(pokemon).filter(k => k !== "name" && k !== "image")
        return (
            <div key={index} className='pokemon'>
                <h4> {pokemon.name}:</h4>
                <ul>
                    {keys.map((key, index) => {
                    return <li key={index}>{key}: {pokemon[key]}</li>
                    })}
                </ul>
                <img src={pokemon.image} alt="image" />
            </div>
        )
    }
    if (pokemons === undefined) {
        return <h3>Loading...</h3>
    }
    return (
    <div>
        <SweetPagination
            currentPageData={setCurrentPageData}
            dataPerPage={dataPerPage}
            getData={pokemons}
            navigation={true}
        />
        <div className='pokemons' >
        {currentPageData
            .sort((p1,p2) => sortMethods[sort].method(p1.number, p2.number))
            .filter(p => !filter || filter === p["type_one"])
            .map((pokemon, index)=> {
                return <DisplayAttributes index={index} pokemon={pokemon}></DisplayAttributes>
            })}
        </div>
    </div>
    );
}
