import React, {useEffect, useState} from "react";
import {loadAllPokemons, loadImage} from "../LoadData";
import {Pagination} from "./Pagination";

export let DisplayPokemons = ({pokemons, keys, filter, sort}) => {
      const sortMethods = {

    none: { method: (a, b) => 0 },
    ascending: { method: (a, b) => (a > b ? 1 : -1) },
    descending: { method: (a, b) => (a > b ? -1 : 1) },
  };

    let DisplayAttributes = ({p}) => {
        return (
            <div>
                <h4> {p.name}:</h4>
                <ul>
                    {keys.map((key, index) => {
                    return <li key={index}>{key}: {p[key]}</li>
                    })}
                </ul>
                <img src={p.image} alt="imane" />
            </div>
        )
    }
    if (pokemons === undefined) return

    return (

        <div >
            {pokemons
                // .sort((p1,p2) => sortMethods[sort].method(p1.number, p2.number))
                .map((p, index)=> {
                    return filter === "" || p["type_one"] === filter ?
                        <div key={index}>
                            <DisplayAttributes p={p}></DisplayAttributes>
                        </div> : <></>
                })}
        </div>
    );
}
