export let Filter = ({setFilter}) => {
    let id = null;
    let handleSetFilter = (event) => {
        clearTimeout(id)
        id = setTimeout(() => {
            setFilter(event.target.value)
        }, 300)
    }

    return (
        <div className="container">
            {/*<input id="pageSize" type="text" placeholder="Page Size"/>*/}
            <input onMouseUp={handleSetFilter} type="text" placeholder="filter"/>
        </div>
    );
}
