export let Pagination = ({ page, setPage, pages, loading}) => {

    return (
       <div className='content' >
           <div className='footer'>
               <button onClick={() => setPage( page-1)} disabled={page === 1 || loading} >prev</button>
               <div className='page-number'>{page}/{pages}</div>
               <button onClick={() => setPage(page+1)} disabled={page === pages || loading} >next</button>
           </div>
            insert page:<input type="number" name="page"  min="1" max={pages} onChange={(event) => setPage(event.target.value) }/>


       </div>

    );
}
