import {useEffect, useState} from "react";
import {loadAllPokemons} from "../LoadData";

export let DisplayPokemons = ({pokemons, keys, filter, sort}) => {
    let [sortedData, setSortedData] = useState(pokemons)
      const sortMethods = {

    none: { method: undefined },
    ascending: { method: (a, b) => (a > b ? 1 : -1) },
    descending: { method: (a, b) => (a > b ? -1 : 1) },
  };
    let DisplayAttributes = ({p}) => {
        return (

            <ul>
                {keys.map((key, index) => {
                return <li key={index}>{key}: {p[key]}</li>
                })}
            </ul>

        )
    }


    if (pokemons === undefined) return

    return (
        <div >
            {pokemons.sort((p1,p2) => sortMethods[sort].method(p1["name"], p2["name"])).map((p, index)=> {
                return filter === "" || p["type_one"] === filter ?
                    <div key={index}>
                        <h4> {p.name}:</h4>
                        <DisplayAttributes p={p}></DisplayAttributes>:<></>

                    </div>:<></>
            })}
        </div>
    );
}
