export let Theme = ({setTheme, isDark}) => {

  const lightTheme = {
    backgroundColor : "white",
    color : "black"
  }

  const darkTheme = {
    backgroundColor : "black",
    color : "white"
  }
    return (
        <div className="container">
            <div className='secondary-title'>filter:</div>
            <button  style={isDark?lightTheme:darkTheme} onClick={()=>setTheme(toggle =>!toggle)}>{isDark ? 'light' : 'dark'} mode</button>

        </div>
    );
}
