import React from "react";


export let Pokemons = ({pokemons}) => {
    let Pokemon = ({pokemon, index}) => {

        let keys = Object.keys(pokemon).filter(k => k !== "name" && k !== "image")
        return (
            <div key={index} className='pokemon'>
                <div className="secondary-title"> {pokemon.name}:</div>
                <ul>
                    {keys.map((key, index) => {
                        return <li key={index}>{key}: {pokemon[key]}</li>
                    })}
                </ul>
                {/*since image may not exist, I should check that the url image exist by loading it first and check the result*/}
                {pokemon.image  && <img src={pokemon.image} alt="image"/>}
            </div>
        )
    }

    if (pokemons === undefined || pokemons.length === 0) {
        return  <div className='content'> <h3>{pokemons === undefined ? "Loading...": "No data found."}</h3> </div>
    }
    return (
        <div className='content' >
            {pokemons
            .map((pokemon, index)=> {
                return <Pokemon index={index} pokemon={pokemon}></Pokemon>
            })
            }
        </div>
    );
}
