import React, {useState} from "react";
import "./assets/Styles.css";
import {LoadChatForm} from "./components/LoadChatForm";
import {DisplayChat} from "./components/DisplayChat";

function App() {

    let [pokemonImage, setPokemonImage] = useState(undefined)

    let loadChat = (chatID) => {
        setPokemonImage(undefined)
        debugger
        fetch(`/icon/{chatID}`)
            .then((res) => {
                if(!res.ok) {
                    throw Error(res.statusText)
                }
                return res.blob()
            })

            .catch((err) => {
                console.log('Error occurred while loading chat: ' + err)
            })
    }

    return (
        <div className="container">
            <h1>Pokedex</h1>
            <LoadChatForm loadChat={chatID => loadChat(chatID)}></LoadChatForm>
            <DisplayChat pokemonImage={pokemonImage}></DisplayChat>
        </div>

    );

}

export default App;
