export let loadPokemonsSize = () => {
    return fetch(`/api/size`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }
            return res.text()
        })
        .then((data) => {
            return  parseInt(data)
        })
        .catch((err) => {
            console.log('Error occurred while loading pokemons: ' + err)
        })
}

let loadImage = (name) => {

    return fetch(`/api/icon/${name}`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }
            return res.text()
        })
        .then((data) => {
            return data
        })
        .catch((err) => {
            console.log('Error occurred while loading image: ' + err)
        })
}

export let loadPokemonsWithImages = (page,pageSize, sort, filter) => {
       return loadPokemons(page,pageSize, sort, filter).then(data => {
           if (!data) {
               return []
           }
           let promisesArray = data.map( pokemon => {
                return loadImage(pokemon["name"].toLowerCase()).then(image => {
                    return {...pokemon, image};
                });
           })
           return Promise.all(promisesArray).then(pokemons => {
               return {pokemons: pokemons, total_size: data.total_size}
           });
      })
}
export let loadPokemons = ( page,pageSize, sort, filter) => {
        return fetch(`/api?page=${page}&pageSize=${pageSize}&sort=${sort}&filter=${filter}`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }

            return res.json()
        })
        .catch((err) => {
            console.log('Error occurred while loading image: ' + err)
        })
}
