import React, { useState } from "react";
import SweetPagination from "sweetpagination";


export let DisplayPokemons = ({pokemons, keys, filter, sort, currentPageData}) => {
    const sortMethods = {
        none: { method: (a, b) => null },
        ascending: { method: (a, b) => (a > b ? 1 : -1)  },
        descending: { method: (a, b) => (a > b ? -1 : 1) },
  };
    let DisplayAttributes = ({pokemon}) => {
        if (pokemon === undefined) {
            return <></>
        }
        return (
            <div>
                <h4> {pokemon.name}:</h4>
                <ul>
                    {keys.map((key, index) => {
                    return <li key={index}>{key}: {pokemon[key]}</li>
                    })}
                </ul>
                <img src={pokemon.image} alt="image" />
            </div>
        )
    }
    if (currentPageData === undefined) {
        return <h3>Loading...</h3>
    }
    return (
    <div className='pokemons'>

        {currentPageData
            .sort((p1,p2) => sortMethods[sort].method(p1.number, p2.number))
            .map((pokemon, index)=> {
                return !filter || filter === pokemon["type_one"] ?
                    <div key={index}>
                       <DisplayAttributes pokemon={pokemon}></DisplayAttributes>
                    </div> : null
            })}
    </div>
    );
}
