import React, { useState } from "react";
import SweetPagination from "sweetpagination";


export let Pagination = ({pokemons, keys, filter, sort}) => {
  const [currentPageData, setCurrentPageData] = useState(new Array(2).fill());
      let DisplayAttributes = ({pokemon}) => {
         if (pokemon === undefined) {
             return <></>
        }
        return (
            <div>
                <h4> {pokemon.name}:</h4>
                <ul>
                    {keys.map((key, index) => {
                    return <li key={index}>{key}: {p[key]}</li>
                    })}
                </ul>
                <img src={pokemon.image} alt="imane" />
            </div>
        )
    }

  return (
    <div>
        <SweetPagination
            currentPageData={setCurrentPageData}
            dataPerPage={10}
            getData={pokemons}
            navigation={true}
        />
        {currentPageData
            // .sort((p1,p2) => sortMethods[sort].method(p1.number, p2.number))
            .map((p, index)=> {
                return <div key={index}>
                        <DisplayAttributes p={p}></DisplayAttributes>
                    </div>
            })}
    </div>
  );
}
