import "./assets/Styles.css";
import React, {useEffect, useState} from "react";
import {
    loadPokemonsSize,
    loadPokemonsWithImages
} from "./utils/loadData";
import {DisplayPokemons} from "./components/DisplayPokemons";
import {Toolbar} from "./components/Toolbar/Toolbar";
import {Pagination} from "./components/Pagination";

let App = () => {
    const isSystemDark = window.matchMedia("(prefers-color-scheme: dark)").matches
    const [isDark,setIsDark] = useState(isSystemDark)
    const [dataPerPage, setDataPerPage] = useState(5)
    const [size, setSize] = useState(0)
    const [pokemons, setPokemons] = useState(undefined)
    const [filter, setFilter] = useState("")
    const [sort, setSort] = useState("none");
    const [page, setPage] = useState(1);

    const lightTheme = {backgroundColor: "white", color: "black"}
    const darkTheme = {backgroundColor : "black", color : "white"}
    const sortMethods = {
        none: { method: (a, b) => null },
        ascending: { method: (a, b) => (a > b ? 1 : -1)  },
        descending: { method: (a, b) => (a > b ? -1 : 1) },
    };
    let handlePageChanged = (change) => {
        setPokemons(undefined)
        loadPokemonsWithImages(page+change, dataPerPage, sort, filter).then(data => {
            setPokemons(data)
            setPage(page + change)
        })
    }
    let handleSort = (value) => {
        setPokemons(undefined)
         loadPokemonsWithImages(1, dataPerPage, value).then(data => {
            setPokemons(data)
            setPage(1)
            setSort(value)

        })
    }
    let handleFilter = (value) => {
        setPokemons(undefined)
         loadPokemonsWithImages(1, dataPerPage, sort, value).then(data => {
            setPokemons(data)
            setPage(1)
            setFilter(value)

        })
    }
    useEffect(() => {
        loadPokemonsWithImages(page, dataPerPage).then(data => setPokemons(data))
        loadPokemonsSize().then(data => setSize(data ? data : 0))

    }, []);


    return (
        <div className="container" style={isDark ? darkTheme : lightTheme }>

            <Toolbar setFilter={(value) => handleFilter(value)}
                     setSort={(value) => handleSort(value)}
                     setDataPerPage={(value) => setDataPerPage(value)}
                     setTheme={(value) => setIsDark(value)}>
            </Toolbar>

            <DisplayPokemons pokemons={pokemons}
                          filter={filter}
                          sort={sort}
                          dataPerPage={dataPerPage}
                          size={size}>
            </DisplayPokemons>
            <Pagination setPage={(value) => handlePageChanged(value)}
                        page={page}
                        pages={Math.ceil(size/dataPerPage)}
                        loading={!pokemons}>
            </Pagination>
        </div>

    );

}

export default App;
