import React, { useState } from "react";
import SweetPagination from "sweetpagination";


export let Pagination = ({pokemons, keys, filter, sort, images}) => {
  const [currentPageData, setCurrentPageData] = useState(new Array(2).fill());
  // Example items, to simulate fetching from another resources.
      let DisplayAttributes = ({p}) => {
        return (
            <div>
                {/*<h4> {p.name}:</h4>*/}
                <ul>
                    {keys.map((key, index) => {
                    return <li key={index}>{key}: {p[key]}</li>
                    })}
                </ul>
                <img src={images.get(p.name)} alt="imane" />
            </div>
        )
    }

  return (
    <div>
         <SweetPagination
            currentPageData={setCurrentPageData}
            dataPerPage={10}
            getData={pokemons}
            navigation={true}
          />

            {currentPageData
                // .sort((p1,p2) => sortMethods[sort].method(p1.number, p2.number))
                .map((p, index)=> {
                    return <div key={index}>
                            <DisplayAttributes p={p}></DisplayAttributes>
                        </div>
                })}

    </div>
  );
}
