let loadAllPokemons = () => {
    return fetch(`/api/`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }
            return res.text()
        })
        .then((data) => {
            return JSON.parse(data)
        })
        .catch((err) => {
            console.log('Error occurred while loading pokemons: ' + err)
        })
}

let loadImage = (name) => {

    return fetch(`/api/icon/${name}`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }
            return res.text()
        })
        .then((data) => {
            return data
        })
        .catch((err) => {
            console.log('Error occurred while loading image: ' + err)
        })
}


export let loadPokemonsWithImages = (name) => {
       return loadAllPokemons()
      .then(data => {
        let promisesArray = data.map( pokemon => {
            return loadImage(pokemon["name"].toLowerCase()).then(image => {
                return {...pokemon, image};
            });
        })
        return Promise.all(promisesArray);
      })
}
export let checkImage = (url) => {
  const request = new XMLHttpRequest();
  request.open("GET", url, true);
  request.send();
  request.onload = () => {
    if (request.status === 200) //if(statusText == OK)
    {
      console.log("image exists");
    } else {
      console.log("image doesn't exist");
    }
  }
}
