import {Sort} from "./Sort";
import {Filter} from "./Filter";
import {DataPerPge} from "./DataPerPge";
import {Theme} from "./Theme";
import {useState} from "react";

export let Toolbar = ({setFilter, setSort, setDataPerPage, setTheme, isDark, size}) => {
    const [page, setPage] = useState(0);

    let handleNext = () => {

        setPage(page-1)

    }

    let handlePrev = () => {
        setPage(page+1)
    }

    return (
       <div className="toolbar">
            <Sort  setSort={setSort}></Sort>
            <Filter setFilter={setFilter}></Filter>
            <DataPerPge setDataPerPage={setDataPerPage}></DataPerPge>
           <Theme isDark={isDark} setTheme={setTheme}></Theme>
           <div className='pagination'>
               <button onClick={handlePrev} disabled={page === 1} >prev</button>
               <div className='page'>{page}</div>
               <button onClick={handleNext} disabled={page === size} >next</button>

           </div>

       </div>
    );
}
