import React, {useState} from "react";
import "./assets/Styles.css";
import {LoadChatForm} from "./components/LoadChatForm";
import {DisplayChat} from "./components/DisplayChat";

function App() {

    let [chatConfiguration, setChatConfiguration] = useState(undefined)

    let loadChat = (chatID) => {
        setChatConfiguration(undefined)

        fetch(`/icon/{chatID}`)
            .then((res) => {
                if(!res.ok) {
                    throw Error(res.statusText)
                }
                return res.json()
            })
            .then((data) => {
                setChatConfiguration(data.message)
            })
            .catch((err) => {
                console.log('Error occurred while loading chat: ' + err)
            })
    }

    return (
        <div className="container">
            <h1>Pokedex</h1>
            <LoadChatForm loadChat={chatID => loadChat(chatID)}></LoadChatForm>
            <DisplayChat chatConfiguration={chatConfiguration}></DisplayChat>
        </div>

    );

}

export default App;
