import React, { useState } from "react";


export let DisplayPokemons = ({pokemons, filter, sort, dataPerPage, size}) => {

    const sortMethods = {
        none: { method: (a, b) => null },
        ascending: { method: (a, b) => (a > b ? 1 : -1)  },
        descending: { method: (a, b) => (a > b ? -1 : 1) },
  };
    let DisplayAttributes = ({pokemon, index}) => {
        if (pokemon === undefined) {
            return <></>
        }

        let keys = Object.keys(pokemon).filter(k => k !== "name" && k !== "image")
        return (
            <div key={index} className='pokemon'>
                <h4> {pokemon.name}:</h4>
                <ul>
                    {keys.map((key, index) => {
                        return <li key={index}>{key}: {pokemon[key]}</li>
                    })}
                </ul>
                {/*since image may not exist, I should check that the url image exist by loading it first and check the result*/}
                <img src={pokemon.image} alt="image"/>
            </div>
        )
    }
    if (pokemons === undefined) {
        return <h3>Loading...</h3>
    }
    return (
    <div>
        <div className='pokemons' >
        {pokemons
            .sort((p1,p2) => sortMethods[sort].method(p1.number, p2.number))
            .filter(p => !filter || filter === p["type_one"])
            .map((pokemon, index)=> {
                return <DisplayAttributes index={index} pokemon={pokemon}></DisplayAttributes>
            })}
        </div>
    </div>
    );
}
