export let Filter = ({setPageSize}) => {

    let handleSetPageSize = () => {
        let size = document.getElementById("pageSize").value.trim()
        setPageSize(size)
    }

    return (
        <div className="container">
            <input id="pageSize" type="text" placeholder="Page Size"/>
            <input onClick={handleSetPageSize} type="submit" value="set"/>
        </div>
    );
}
