import React, {useEffect, useState} from "react";
import "./assets/Styles.css";
import {Sort} from "./components/Sort";
import {DisplayPokemons} from "./components/DisplayPokemons";
import {Filter} from "./components/Filter";
import {loadAllPokemons, loadImage} from "./LoadData";
import {map} from "react-bootstrap/ElementChildren";
import {Toolbar} from "./components/Toolbar";
import {Pagination} from "./components/Pagination";
function App() {


    let [images, setImages] = useState(new Map())
    let [loading, setLoading] = useState(true)
    let [pokemons, setPokemons] = useState(undefined)
    let [filter, setFilter] = useState("")
    let [sort, setSort] = useState("none");

    let keys = ["number", "name", "type_one", "type_two", "total", "hit_points", "attack", "defense", "special_attack", "special_defense", "speed", "generation", "legendary"]

    useEffect(() => {
        setLoading(true)
        loadAllPokemons().then(data => {
            setPokemons(data)
            for (let pokemon of data) {

                loadImage(pokemon["name"].toLowerCase()).then(image => {
                    setImages({...images, image})

                })
            }
        setLoading(false)

        })


    }, []);

    return (
        <div className="container">
            <h1>Pokedex</h1>
            { loading === true  ? <div> loading...</div>:<Pagination keys={keys} pokemons={pokemons} images={images}></Pagination> }
            {/*<Toolbar setFilter={(value) => setFilter(value)} setSort={(value) => setSort(value)}></Toolbar>*/}
            {/*<DisplayPokemons keys={keys} pokemons={pokemons} filter={filter} sort={sort} ></DisplayPokemons>*/}
        </div>

    );

}

export default App;
