import React, {useEffect, useState} from "react";
import "./assets/Styles.css";
import {LoadChatForm} from "./components/LoadChatForm";
import {DisplayPokemons} from "./components/DisplayPokemons";

function App() {

    let [pokemonImage, setPokemonImage] = useState(undefined)
    let [pokemons, setPokemons] = useState(undefined)
    let keys = ["number", "name", "type_one", "type_two", "total", "hit_points", "attack", "defense", "special_attack", "special_defense", "speed", "generation", "legendary"]
    let loadAllPokemons = () => {
        fetch(`/api/`)
            .then((res) => {
                if (!res.ok) {
                    throw Error(res.statusText)
                }
                return res.text()
            })
            .then((data) => {
                setPokemons(JSON.parse(data))
            })
            .catch((err) => {
                console.log('Error occurred while loading chat: ' + err)
            })
    }

    let loadChat = (chatID) => {
        setPokemonImage(undefined)

        fetch(`/api/icon/${chatID}`)
            .then((res) => {
                if (!res.ok) {
                    throw Error(res.statusText)
                }
                return res.text()
            })
            .then((data) => {
                setPokemonImage(data)
            })
            .catch((err) => {
                console.log('Error occurred while loading chat: ' + err)
            })
    }
    let loadImages = () => {
        for (let pokemon of pokemons) {

             console.log(pokemons)
        }


    }
    useEffect(() => {
        loadAllPokemons()

    }, []);

    return (
        <div className="container">
            <h1>Pokedex</h1>
            <LoadChatForm loadChat={chatID => loadChat(chatID)}></LoadChatForm>
            <DisplayPokemons keys={keys} pokemons={pokemons}></DisplayPokemons>
        </div>

    );

}

export default App;
