export function loadAllPokemons() {
    return fetch(`/api/`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }
            return res.text()
        })
        .then((data) => {
            return JSON.parse(data)
        })
        .catch((err) => {
            console.log('Error occurred while loading pokemons: ' + err)
        })
}

export let loadImage = (name) => {

    return fetch(`/api/icon/${name}`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }
            return res.text()
        })
        .then((data) => {
            return data
        })
        .catch((err) => {
            console.log('Error occurred while loading image: ' + err)
        })
}


