import "./assets/Styles.css";
import React, {useEffect, useState} from "react";
import {loadAllPokemons, loadImage} from "./utils/loadData";
import {DisplayPokemons} from "./components/DisplayPokemons";
import {Toolbar} from "./components/Toolbar/Toolbar";
import SweetPagination from "sweetpagination";

function App() {

    let [dataPerPage, setDataPerPage] = useState(5)
    let [currentPageData, setCurrentPageData] = useState(new Array(2).fill());

    let [pokemons, setPokemons] = useState(undefined)
    let [filter, setFilter] = useState("")
    let [sort, setSort] = useState("none");

    let keys = ["number", "name", "type_one", "type_two", "total", "hit_points", "attack", "defense", "special_attack", "special_defense", "speed", "generation", "legendary"]

    useEffect(() => {
        loadAllPokemons()
          .then(data => {
            let promisesArray = data.map( pokemon => {
                return loadImage(pokemon["name"].toLowerCase()).then(image => {
                    return {...pokemon, image};

                });
            })
            return Promise.all(promisesArray);
          })
            .then((data) => setPokemons(data));


    }, []);

    return (
        <div className="container" style={{height: '100%'}}>
            <div className='title'>Pokedex</div>
            <Toolbar setFilter={(value) => setFilter(value)} setSort={(value) => setSort(value)} setDataPerPage={(value) => setDataPerPage(value)}></Toolbar>
            {pokemons ? <SweetPagination currentPageData={setCurrentPageData} dataPerPage={dataPerPage} getData={pokemons}
                                         navigation={true}/> : <div></div>}
             <DisplayPokemons keys={keys} pokemons={pokemons} filter={filter} sort={sort} currentPageData={currentPageData}></DisplayPokemons>
        </div>

    );

}

export default App;
