export let loadPokemonsSize = () => {
    return fetch(`/api/size`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }
            return res.text()
        })
        .then((data) => {
            return  parseInt(data)
        })
        .catch((err) => {
            console.log('Error occurred while loading pokemons: ' + err)
        })
}

let loadImage = (name) => {

    return fetch(`/api/icon/${name}`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }
            return res.text()
        })
        .then((data) => {
            return data
        })
        .catch((err) => {
            console.log('Error occurred while loading image: ' + err)
        })
}

export let loadPokemonsWithImages = (count) => {
       return loadPokemons(count)
      .then(data => {
          debugger
        let promisesArray = data.map( pokemon => {
            return loadImage(pokemon["name"].toLowerCase()).then(image => {
                return {...pokemon, image};
            });
        })
        return Promise.all(promisesArray);
      })
}
export let loadPokemons = (page,pageSize ) => {
        return fetch(`/api?page=${page}&pageSize=${5}`)
        .then((res) => {
            if (!res.ok) {
                throw Error(res.statusText)
            }

            return res.json()
        })
        .then((data) => {

            return data
        })
        .catch((err) => {
            console.log('Error occurred while loading image: ' + err)
        })
}
