import React, { useState } from "react";
import SweetPagination from "sweetpagination";


export let DisplayPokemons = ({pokemons, keys, filter, sort}) => {
  const [currentPageData, setCurrentPageData] = useState(new Array(2).fill());
    const sortMethods = {
        none: { method: (a, b) => null },
        ascending: { method: (a, b) => (a > b ? 1 : -1)  },
        descending: { method: (a, b) => (a > b ? -1 : 1) },
  };
      let DisplayAttributes = ({pokemon}) => {
         if (pokemon === undefined) {
             return <></>
        }
        return (
            <div>
                <h4> {pokemon.name}:</h4>
                <ul>
                    {keys.map((key, index) => {
                    return <li key={index}>{key}: {pokemon[key]}</li>
                    })}
                </ul>
                <img src={pokemon.image} alt="image" />
            </div>
        )
    }

  return (
    <div>
        <SweetPagination
            currentPageData={setCurrentPageData}
            dataPerPage={10}
            getData={pokemons}
            navigation={true}
        />
        {currentPageData
            .sort((p1,p2) => sortMethods[sort].method(p1.number, p2.number))
            .map((pokemon, index)=> {
                return !filter || filter === pokemon["type_one"] ? <div key={index}>
                        <DisplayAttributes pokemon={pokemon}></DisplayAttributes>
                    </div> : null
            })}
    </div>
  );
}
