export let Sort = ({loadChat}) => {

    let handleLoadChat = () => {
        let chatID = document.getElementById("chatID").value.trim()
        loadChat(chatID)
    }

    return (
        <div className="container">
            <input id="chatID" type="text" placeholder="Chat ID.."/>
            <input onClick={handleLoadChat} type="submit" value="Load Chat"/>
        </div>
    );
}
