                }
                return res.text()
            })
            .then((data) => {
                debugger
                setPokemonImage(data)
            })
            .catch((err) => {
                console.log('Error occurred while loading chat: ' + err)
            })
    }
    let loadImages = () => {

        for (const pokemon in pokemons) {
            debugger
            console.log(pokemon)
        }
    }
    loadAllPokemons()
    loadImages()

    return (
        <div className="container">
            <h1>Pokedex</h1>
            <LoadChatForm loadChat={chatID => loadChat(chatID)}></LoadChatForm>
            <DisplayPokemons pokemonImage={pokemonImage}></DisplayPokemons>
        </div>

    );

}

export default App;
